import React, { useState } from "react";
import { Layout, Menu } from 'antd';
import {
  LineChartOutlined,
  FormOutlined,
  OrderedListOutlined,
  BellOutlined,
  UsergroupAddOutlined,
  ShopOutlined
} from '@ant-design/icons';
import 'antd/dist/antd.css';
import '../../global.css';

const { Sider } = Layout;
const { SubMenu } = Menu;

function MenuSite(props){

  function redirect(path){
    window.location.href = path;
  }

	return(
		    <Sider className="menu" trigger={null} collapsible collapsed={props.open}>
          <div className="logo" />
          <Menu className="menu" mode="inline">
            <Menu.Item className="i-menu" key="1" icon={<LineChartOutlined />}>
              Relatório
            </Menu.Item>

            <Menu.Item className="i-menu" key="2" icon={<UsergroupAddOutlined />}>
              Meus clientes
            </Menu.Item>

            <Menu.Item className="i-menu" key="3" icon={<BellOutlined />}>
              Pedidos
            </Menu.Item>

            <Menu.Item className="i-menu" key="5" icon={<ShopOutlined />}>
              Pdv
            </Menu.Item>

            <SubMenu className="i-menu" key="sub1" icon={<FormOutlined />} title="Cadastros">
              <Menu.Item className="i-menu" key="sub1-1" onClick={() => redirect('/addProduct')}>Produto</Menu.Item>
              <Menu.Item className="i-menu" key="sub1-2">Categoria</Menu.Item>
              <Menu.Item className="i-menu" key="sub1-3">Tamanho</Menu.Item>
              <Menu.Item className="i-menu" key="sub1-4">Borda</Menu.Item>
              <Menu.Item className="i-menu" key="sub1-5">Cupom</Menu.Item>
              <Menu.Item className="i-menu" key="sub1-6">Promoção</Menu.Item>
            </SubMenu>

            <SubMenu className="i-menu" key="sub2" icon={<OrderedListOutlined />} title="Listagens">
              <Menu.Item className="i-menu" key="sub2-1" onClick={() => redirect('/products')}>Produtos</Menu.Item>
              <Menu.Item className="i-menu" key="sub2-2">Categorias</Menu.Item>
              <Menu.Item className="i-menu" key="sub2-3">Tamanhos</Menu.Item>
              <Menu.Item className="i-menu" key="sub2-4">Bordas</Menu.Item>
              <Menu.Item className="i-menu" key="sub2-5">Cupons</Menu.Item>
              <Menu.Item className="i-menu" key="sub2-6">Promoções</Menu.Item>
            </SubMenu>


          </Menu>
        </Sider>
	);
}

export default MenuSite;
