import React, { useState, useEffect } from "react";
import axios from "axios";

import { 
	Layout,
	Button, 
	Row, 
	Col,
	message,
	Spin,
	Steps,
	Card,
	Typography
} from 'antd';
import {
  PlusOutlined
} from '@ant-design/icons';
import 'antd/dist/antd.css';
import '../../global.css';

import HeaderSite from "../../components/Header";
import MenuSite from "../../components/Menu";
import FooterSite from "../../components/Footer";

const { Content } = Layout;
const { Step } = Steps;
const { Title } = Typography;

const BASE_URL = "http://localhost:4020/";

function Pdv() {
	const [expand, setExpand] = useState(false);
	const [stepCurrent, setStepCurrent] = useState(0);
	const [isGuirland, setIsGuirland] = useState(false);
	const [loading, setLoading] = useState(false);

	const [dataCategory, setDataCategory] = useState([]);

	//ID's que compõe o pedido
	const [idCategoryOrder, setIdCategoryOrder] = useState(null);

	useEffect(() => {
		axios.get(BASE_URL+"category").then((response) => {
			let array = [];
			response?.data.forEach((category) => {
				array.push({
					id: category?.id_category,
					name: category?.name_category,
					check: false
				})
			})
			setDataCategory(array);						
		}).catch((error) => {
			console.log("BUGOU: "+ error);
		});
	}, []);


	const updateStepCurrent = () => {
		if(stepCurrent !== 3){
			setStepCurrent(stepCurrent + 1);
		}else{
			console.log("Abrir modal para inserir dados de entrega do cliente.")
		}

	}

	const onChangeCategory = (idCategory) => {
		console.log(idCategory);

	}


	return (
		<div>
			<Spin size="large" spinning={loading}>
				<Layout>
					<MenuSite open={expand} />
			        <Layout className="site-layout">
				        <HeaderSite title={'Ponto de venda'} isListView={true} expandMenu={expand} updateExpandMenu={() => setExpand(!expand)} />
				        <Content className="container-main">
				    		<Row>
				    			<Col span={24}>
				    				<Steps current={stepCurrent}>
	          							<Step key={0} title="Categoria" />
	          							<Step key={1} title="Sabor" />
	          							<Step key={2} title={isGuirland ? "Opção":"Tamanho"} />
	          							<Step key={3} title="Resumo" />
	      							</Steps>
	      							 <div className="steps-content">
	      							 	{
	      							 		stepCurrent === 0 ? (
	      							 			<Row gutter={{ xs: 8, sm: 16, md: 24, lg: 32 }}>
	      							 				{
	      							 					dataCategory?.map((category) => {
	      							 						return(
																<Col span={8}>
				      							 					<Card
				      							 						hoverable 
				      							 						className={category?.check ? "check-card-pdv" : "card-pdv"}
				      							 						onClick={() => onChangeCategory(category?.id)}
				      							 					>
																    	<Title level={3}>
																			{category?.name}
																    	</Title>
																    	<p>
																    		Clique para escolher.
																    	</p>
																    </Card>
				      							 				</Col>
	      							 						)
	      							 					})
	      							 				}

	      							 			</Row>
	      							 		):
	      							 		stepCurrent === 1 ? (
	      							 			<p>Sabor</p>
	      							 		):
	      							 		stepCurrent === 2 ? (
	      							 			<p>Tamanho</p>
	      							 		):(
	      							 			<p>Resumo</p>
	      							 		)
	      							 	}
	      							 </div>

	      							 <div style={{ marginTop: "15px" }}>
										<Button 
											onClick={() => updateStepCurrent()} 
											shape="round" 
											className="button" 
											style={{ margin: "5px" }}
										>
										    {stepCurrent !== 3 ? "Seguir":"Finalizar pedido"}
										</Button>
										{
											stepCurrent !== 0 && (
												<Button 
													onClick={() => setStepCurrent(stepCurrent - 1)} 
													shape="round" 
													className="button-cancel" 
													style={{ margin: "5px" }}
												>
										    		Voltar
												</Button>
											)
										}	
	      							 </div>

				    			</Col>
				    		</Row>    		    
				        </Content>
			          <FooterSite />
			        </Layout>
		      	</Layout>
		    </Spin>
  		</div>
  	);
}

export default Pdv;
